import { createESTest } from "escss-estest";

function ESTest(input, type, message = "[demo-is-number] Surprise!! I changed input check from 'number' to 'boolean' 😎 ") {
 return createESTest(input, type, message)
}

export function isNumber(input) {
  {
    ESTest(input, 'boolean')
  }

  return input
}