import { createESTest } from "escss-estest";
import { isNumber } from "mike-demo-is-number";

function ESTest(input, type, message = "[demo-is-positive] Kinda feels like it’s time to switch from `positive()` to `negative()` ❤️") {
 return createESTest(input, type, message)
}

export function isPositive(input) {
  {
    ESTest(input, 'number').negative()
  }

  return isNumber(input)
}